import React, { useState } from "react";
import "./track.css";

function Track({ track }) {
  const [currentAudio, setCurrentAudio] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);

  const handleClick = (preview) => {
    const audio = new Audio(preview);

    // If there's already a current audio playing
    if (currentAudio) {
      // Pause the current audio
      currentAudio.pause();
      // Reset its playback time
      currentAudio.currentTime = 0;
      // Update isPlaying state
      setIsPlaying(false);
    }

    // Play the new audio
    audio.play();

    // Update state variables
    setCurrentAudio(audio);
    setIsPlaying(true);
  };

  return (
    <div className="track">
      <div className="track-img">
        <img src={track.album.cover_small} alt={track.title} />
      </div>
      <div className="track-info">
        <p className="track-title">{track.title}</p>
        <p className="track-artist">{track.artist.name}</p>
        <p className="track-album">{track.album.title}</p>
      </div>
      <div className="track-control">
        <button onClick={() => handleClick(track.preview)}>Play</button>
      </div>
    </div>
  );
}

export default Track;

