import React, { useState } from "react";
import "./track.css";

function Track({ track }) {
  const [currentAudio, setCurrentAudio] = useState(null);

  const handleClick = (prev) => {
    // Pause the currently playing audio, if any
    if (currentAudio) {
      currentAudio.pause();
    }

    // Create a new audio element for the selected track
    const audio = new Audio(prev);

    audio.play();

    setCurrentAudio(audio);
  };

  return (
    <div className="track">
      <div className="track-img">
        <img src={track.album.cover_small} alt={track.title} />
      </div>
      <div className="track-info">
        <p className="track-title">{track.title}</p>
        <p className="track-artist">{track.artist.name}</p>
        <p className="track-album">{track.album.title}</p>
      </div>
      <div className="track-control">
        <button onClick={() => handleClick(track.preview)}>Play</button>
      </div>
    </div>
  );
}

export default Track;
