import React, { useState } from "react";
import { searchTracks } from "./services/deezer";
import Track from "./components/Track/Track";
import SearchTracks from "./components/SearchTracks/SearchTracks";
import "./styles/App.css";

function App() {
  const [tracks, setTracks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [currentAudio, setCurrentAudio] = useState(null);

  const search = async (query) => {
    setLoading(true);
    setError(null);
    try {
      const data = await searchTracks(query);
      console.log("App js data ", data);
      setTracks(data);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleClick = (previewTrack) => {
    console.log("Click ", previewTrack);
    console.log(currentAudio);

    if (currentAudio) {
      currentAudio.pause();
      return;
    }

    const audio = new Audio(previewTrack);

    console.log(audio);

    setCurrentAudio(audio);
    audio.play();
  };

  return (
    <div className="app">
      <h1 className="title">Deezer Track Search</h1>
      <SearchTracks onSubmit={search} />
      {error && <p className="error">{error}</p>}
      {!loading && (
        <div className="track-list">
          {tracks.map((track) => (
            <Track key={track.id} track={track} handleClick={handleClick} />
          ))}
        </div>
      )}
      {loading && <p>Loading</p>}
    </div>
  );
}

export default App;
